<template>  <div id="app" class="container mx-auto p-4">    <h1 class="text-2xl font-bold mb-4">Управление контактами</h1>    <SearchBar v-model="searchQuery"/>    <ContactForm @saveContact="saveContact" :editContact="editContact" @cancelEdit="cancelEdit"/>    <ContactList :contacts="filteredContacts" :preparedEditContact="preparedEditContact" @editContact="setEditContact"                 @deleteContact="deleteContact"    />  </div></template><script lang="ts">import { defineComponent, ref, computed, watch } from 'vue';import SearchBar from './components/SearchBar.vue';import ContactForm from './components/ContactForm.vue';import ContactList from './components/ContactList.vue';import { Contact } from '@/types/types';export default defineComponent({  name: 'App',  components: {    SearchBar,    ContactForm,    ContactList  },  setup() {    const contacts = ref<Contact[]>(JSON.parse(localStorage.getItem('contacts') || '[]'));    const searchQuery = ref('');    const editContact = ref<Contact | undefined>(undefined);    const preparedEditContact = computed(() => editContact.value ?? {      id: 0,      name: '',      phone: '',      email: ''    });    const saveContact = (contact: Contact) => {      if (editContact.value) {        const index = contacts.value.findIndex(c => c.id === editContact.value?.id);        if (index !== -1) contacts.value[index] = contact;      } else {        contact.id = Date.now();        contacts.value.push(contact);      }      editContact.value = undefined;    };    const deleteContact = (id: number) => {      contacts.value = contacts.value.filter(contact => contact.id !== id);    };    const setEditContact = (contact: Contact) => {      editContact.value = { ...contact };    };    const cancelEdit = () => {      editContact.value = undefined;    };    const filteredContacts = computed(() => {      return contacts.value.filter(contact =>          contact.name.toLowerCase()              .includes(searchQuery.value.toLowerCase())      );    });    watch(contacts, (newContacts) => {      localStorage.setItem('contacts', JSON.stringify(newContacts));    }, { deep: true });    return {      contacts,      searchQuery,      saveContact,      deleteContact,      setEditContact,      editContact,      preparedEditContact,      filteredContacts,      cancelEdit    };  }});</script>